mapper.select:
select id from foods
and id in( ( select food_id from food_type
and type_id = #{typeId}
) )
and food_name like concat('%',#{search},'%') or description like concat('%',#{search},'%')

SELECT f.id AS food_id, f.food_name, f.description, f.picture, nc.name_en, nc.name_cn, fn.content, fn.status FROM foods f LEFT JOIN food_nut fn ON f.id = fn.food_id AND fn.status = 2 LEFT JOIN nutritional_components nc ON fn.nut_id = nc.id WHERE f.id IN
#{id}
ORDER BY f.id, nc.id;
